package Lec_8;

import java.util.Scanner;

public class forLoopSyntax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      Scanner s= new Scanner(System.in);
      int n= s.nextInt();
      
       for(int i=1; i<=n ;i=i+1) {
    	   System.out.print(i +" ");
       }
       
       
	}

}
